%% Inicialização do ambiente:
close all; clc; clear;

rosshutdown;  % garante reset %[output:7449366c]
rosinit;      % conecta ao master ROS (ex.: CoppeliaSim rosInterface) %[output:97c92088]

% Publisher para mandar juntas do KUKA (7 valores)
pub = rospublisher('/kuka_cmd','std_msgs/Float64MultiArray');
msg = rosmessage(pub);

% Carregando o modelo URDF do robô
iiwa = loadrobot("kukaIiwa14", DataFormat="row");

% Juntas em radianos (Posição inicial da operação)
qr = deg2rad([0 0 0 90 0 -30 0]);

% --- Parâmetros de trajetória ---
dt = 0.02;
vxyz = [0.4 0.4 0.4];
tacc = 0.2;

% --- Orientação e alvo cartesiano ---
T_orientacao = oa2tform([0 1 0], [0 0 -1]);  
endEffectorName = iiwa.BodyNames{end};

% Pose inicial
T_home = getTransform(iiwa, qr, endEffectorName);
p_home = tform2trvec(T_home);

% Alvo cartesiano relativo
p_target = p_home + [0.50  -0.30  0.10];

% --- Trajetória cartesiana reta HOME -> TARGET ---
traj = mstraj([p_home; p_target], vxyz, [], p_home, dt, tacc);

% --- Poses 4x4 ---
N = size(traj,1);
Tp = zeros(4,4,N);
for i = 1:N
    Tp(:,:,i) = trvec2tform(traj(i,:)) * T_orientacao;
end

% IK
ik = inverseKinematics('RigidBodyTree', iiwa);
weights = [0.25 0.25 0.25 1 1 1];
q_start = ik(endEffectorName, Tp(:,:,1),   weights, qr);
q_end   = ik(endEffectorName, Tp(:,:,end), weights, q_start);

% Trajetória em juntas
wps = [q_start' q_end'];            
[q_anim, ~] = trapveltraj(wps, N);  
q_anim = q_anim.';                  

% --- Envio via ROS + Animação local ---
r = rateControl(1/dt);
figure; title('Animando e enviando ao Coppelia via ROS'); %[output:1237e231]
for i = 1:N %[output:group:3e7ee945]
    % Atualiza simulação local (MATLAB)
    show(iiwa, q_anim(i,:), 'PreservePlot', false, 'Frames', 'off'); %[output:1237e231] %[output:1c687ada]
    drawnow;
    
    % Envia para o Coppelia (ROS)
    msg.Data = q_anim(i,:);   % vetor de 7 juntas
    send(pub,msg);

    waitfor(r);
end %[output:group:3e7ee945]


